// Returns the detail user interface that is displayed
// when the node is selected in the tree
var oldEnableEvents = this.enableEvents;
this.modelUI = null;
this.enableEvents = this.enableEvents && !(this.model instanceof Finder);
try {
  if (this.modelUI == null) {
    this.modelUI = this.createModelUI(this.model);
    if (this.modelUI != null && this.modelUI.model == this.model &&
      this.model instanceof Finder && this.model.getAutoSearch() &&
      this.isFinderNode()) {
      // expand node if finder node has auto search
      this.expand();
    }
  }
  else if(this.modelUI instanceof com.conceptwave.system.BaseFinderUserInterface) {
    if (this.modelUI.model.getAutoSearch()) {
      // refresh search
      this.modelUI.searchAction(false);
    }
    else if (this.modelUI.result != null) {
      // force to re-fetch finder result table in browser
      this.modelUI.result.updateList;
    }
  }
}
finally {
  this.enableEvents = oldEnableEvents;
}
return this.modelUI;

