    } else{
        passouLimite = true;
        valorLimiteEstourado = qtdPontosSTBDVRGratis;
    }
}

if (tipoPontoAdicional == "STB_HD_PAGO"){
    if (qtdPontosHDPago + qtdPontosHDPagoExtra < qtdPontosSTBHDPago){
