    if (qtdPontosHDGratis + qtdPontosHDGratisExtra < qtdPontosSTBHDGratis){
        ord.ProductData.qtdPontosHDGratisExtra++;
    } else{
        passouLimite = true;
        valorLimiteEstourado = qtdPontosSTBHDGratis;
    }
}

