var msg = "";

if(atividade == "Dissociar STB Erro"){
    if(order.OriginalData[0].EquipmentData != null && order.OriginalData[0].EquipmentData[0] != null){
        if(order.OriginalData[0].EquipmentData[0].AddPointsEquipmentData){
                    for(var i = 0; i< order.OriginalData[0].EquipmentData[0].AddPointsEquipmentData.length; i++){
                        if(order.OriginalData[0].EquipmentData[0].AddPointsEquipmentData[i].EquipmentPointData){
                           var result = FTTHCommon.mandatoryFieldsManualActivitiesPointEquipment(atividade, order.OriginalData[0].EquipmentData[0].AddPointsEquipmentData[i].EquipmentPointData, tipoOS);
                           if(result != "")
                               msg = msg + "\n Formulário Dados dos Equipamento de Ponta " + i + " deve ser preenchido com os seguintes campos para a retirada: "  + result  + "\n";

                        }
                    }
                }
    }
}

if(msg != ""){
   var doc = new Document ("FTTHCommon.MandatoryMessage");
    doc.fields = msg;
    return doc;
}

return true;

